import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const menuItems = [
  { label: 'Home', path: '/' },
  { label: 'Story', path: '/story' },
  { label: 'Wedding Details', path: '/detail' },
  { label: 'All You Need to Know', path: '/info' },
  { label: 'RSVP', path: '/rsvp' },
  { label: 'Contact Us', path: '/contact' },
];

function Navigation() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  useEffect(() => {
    const offcanvasNavbar = document.getElementById('offcanvasNavbar');
    const backdrop = document.querySelector('.offcanvas-backdrop.show');

    if (!isMenuOpen && backdrop) {
      backdrop.classList.add('custom-backdrop');
      setTimeout(() => {
        backdrop.classList.remove('custom-backdrop');
      }, 300); // 0.3초 후에 배경 페이드 효과 비활성화
    }
  }, [isMenuOpen]);

  return (
    <nav className="navbar fixed-top">
      <div className="container-fluid">
        <a className="navbar-brand" href="#">
          #CH2BECOME1
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="offcanvas"
          data-bs-target="#offcanvasNavbar"
          aria-controls="offcanvasNavbar"
          aria-label="Toggle navigation"
          onClick={toggleMenu}
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div
          className={`offcanvas offcanvas-end ${isMenuOpen ? 'show' : ''}`}
          tabIndex="-1"
          id="offcanvasNavbar"
          aria-labelledby="offcanvasNavbarLabel"
        >
          <div className="offcanvas-header">
            <h5 className="offcanvas-title" id="offcanvasNavbarLabel">
              #CH2BECOME1
            </h5>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="offcanvas"
              aria-label="Close"
              onClick={closeMenu}
            ></button>
          </div>
          <div className="offcanvas-body">
            <ul className="navbar-nav justify-content-end flex-grow-1 pe-3">
              {menuItems.map((item, index) => (
                <li className="nav-item" key={index}>
                  <a className="nav-link" href="#">
                    <Link
                      className="nav_link"
                      to={item.path}
                      onClick={closeMenu}
                    >
                      {item.label}
                    </Link>
                  </a>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navigation;
