import React from "react";
import { useState } from "react";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";

import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

function Place() {

 const [accomodation, setMia] = useState(false);
 const [eat, setEat] = useState(false);
 const [drink, setDrink] = useState(false);
 const [sight, setSight] = useState(false);

 const toggleCategory = (category) => {
  switch (category) {
    case "accomodation":
      setMia(!accomodation);
      break;
    case "eat":
      setEat(!eat);
      break;
    case "drink":
      setDrink(!drink);
      break;
    case "sight":
      setSight(!sight);
      break;
    default:
      break;
  }
};

const categories = [
  { label: "Accomodation", state: accomodation, key: "accomodation" },
  { label: "Eat", state: eat, key: "eat" },
  { label: "Drink", state: drink, key: "drink" },
  { label: "Sightseeing", state: sight, key: "sight" },
];

 return (
  <Container className="container_center" fluid>
   <img className="party_flower" src="/images/nhatrang.jpeg" />
   <h1 style={{ fontFamily: "Lora, italic", marginTop: 30 }}>
    {" "}
    N H A &nbsp; T R A N G &nbsp;
   </h1>
   <p>
    We are so excited to welcome you in our <b>favorite beach city in Vietnam, Nha Trang. </b>
    <br></br>
    Here's what we recommend for your enjoyable stay!
   </p>

    {categories.map((category) => (
        <Button
          key={category.key}
          style={{
            marginRight: "20px",
            backgroundColor: category.state ? "Black" : "transparent",
            color: category.state ? "white" : "black",
          }}
          variant="outline-dark"
          onClick={() => toggleCategory(category.key)}
        >
          {category.label}
        </Button>
    ))}


  </Container>
 );
}

export default Place;
